import React, {useEffect, useState} from 'react';
import { Form } from 'react-bootstrap';
import { Button } from 'react-bootstrap';
import { Alert } from 'react-bootstrap';
import { Link } from 'react-router-dom'
import { useNavigate } from "react-router-dom";
import Axios from 'axios';
import '../App.css';

function Bodyappventasbody(props){
    const [venta, setVenta] = useState([]);
    const [cliente, setCliente] = useState([]);
    const [producto, setProducto] = useState([]);
    const [nombre, setNombre] = useState();
    const [precio, setPrecio] = useState(0);
    const [cantidad, setCantidad] = useState(0);
    const [total, setTotal] = useState(0);
    const navigate = useNavigate();

    const dataCliente = cliente.map((item, i) => 
        <option key={i} value={item.id}>{item.nombre}</option>);

    const dataProducto = producto.map((item, i) => 
        <option onClick={() => cargaProducto(item)} key={i} value={item.id}>{item.nombre}</option>);

    const clickHandler = async (e) =>{
        let subtotal = precio * cantidad
        let ventaAux = [] 
        ventaAux.push(venta)
        let line = <div className='row cltkLineGrilla'><div className='col col-xl-3 col-lg-3 col-md-3 col-sm-3'>{nombre}</div><div className='col col-xl-3 col-lg-3 col-md-3 col-sm-3'>{precio}</div><div className='col col-xl-3 col-lg-3 col-md-3 col-sm-3'>{cantidad}</div><div className='col col-xl-3 col-lg-3 col-md-3 col-sm-3'>{subtotal}</div></div>        
        ventaAux.push(line)
        setVenta(ventaAux)
        setTotal(total + subtotal)
    }

    const changeHandlerCantidad = async (e) =>{
        setCantidad(e.target.value)
    }

    const cargaProducto = async (item) =>{
        setNombre(item.nombre)
        setPrecio(item.precio)
    }

    useEffect(() => {       
        Axios.get("http://localhost:3002/data", { 
            headers: {
                'Access-Control-Allow-Origin' : '*',
                'Access-Control-Allow-Methods': 'GET, POST',
                'Access-Control-Allow-Headers': 'Content-Type'
            }
        }).then((response) => {
            console.log(response.data.cliente)
            console.log(response.data.producto)
            setCliente(response.data.cliente);
            setProducto(response.data.producto);
        }).catch(error => {
           return error     
        });
    }, []);    

    return (
        <div className='sshbodyapp'>
            <div className='sshbodyappContainer'>        
                <div className='row'>
                    <h1>Ventas</h1>
                </div>
                <div className='row'>
                    <h2>Vender</h2>
                </div>
                <div className='row'>
                    <div className='col col-lg-4'>
                        Seleccion Cliente:
                    </div>
               
                    <div className='col col-lg-8'>
                        <select>
                           {dataCliente}
                        </select>
                    </div>
                </div>

                <div className='row'>
                    <div className='col col-lg-4'>
                        Seleccione Producto:
                    </div>
                    <div className='col col-lg-8'>
                        <select>
                            {dataProducto}
                        </select>
                    </div>
                </div>

                <div className='row'>
                    <div className='col col-lg-4'>
                        Nombre:
                    </div>
                    <div className='col col-lg-8'>
                        <input type='text' value={nombre} disabled='true'></input>
                    </div>
                </div>

                <div className='row'>                
                    <div className='col col-lg-4'>
                        Precio:
                    </div>
                    <div className='col col-lg-8'>
                    <input type='text' value={precio} disabled='true'></input>
                    </div>                                                            
                </div>

                <div className='row'>                
                    <div className='col col-lg-4'>
                        Seleccione Cantidad
                    </div>
                    <div className='col col-lg-8'>
                        <input type='number' value={cantidad} onChange={changeHandlerCantidad} min='0' max='1000'></input>
                    </div>                                                            
                </div>

                <div className='row'>                
                    <div className='col col-lg-4'>
                        Agregar a la venta
                    </div>
                    <div className='col col-lg-8'>
                        <button onClick={clickHandler}>Agregar</button>
                    </div>                                                            
                </div>

                <div className='row sabackComponent'>
                        <div className='row saTitulosGrid'>
                            <div className='col col-xl-3 col-lg-3 col-md-3 col-sm-3'>Nombre</div>
                            <div className='col col-xl-3 col-lg-3 col-md-3 col-sm-3'>Precio</div>
                            <div className='col col-xl-3 col-lg-3 col-md-3 col-sm-3'>Cantidad</div>
                            <div className='col col-xl-3 col-lg-3 col-md-3 col-sm-3'>Subtotal</div>
                        </div> 
                        <div className='row saContainerGrid'>               
                            {venta}
                        </div>
                </div>
                <div className='row sabackComponent'>
                    <h3>Total : {total}</h3> 
                </div>
                <div className='row sabackComponent'>
                    <button  className='btn btn-adminsaprivateRegular'>Crear Venta</button>
                </div>
                
            </div>
        </div>                   
    )

}
export default Bodyappventasbody
